settings.custom_css:
/* Layout */
.gform-body.gform_body {
    padding: 64px;
    background-color: #ffffff;
    border-radius: 16px;
    box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 0.25);
    font-size: 18px;
}
#gform_fields_13_4, #gform_fields_13_2{
    --gf-form-gap-y: 0;
}
#gform_fields_13_2 > div:not(#field_13_88, #field_13_89){
    padding-bottom: 40px;
}
#gform_fields_13_2{
    display: block;
    column-count: 2;
    column-gap: 88px;
}
.column-span{
    column-span: all!important;
}
.column-break{
    break-before: column!important;
}
.gfield.column-inline{
    column-span: all!important;
    /*display: inline-flex;*/
    width: calc(100% / 3);
    flex-direction: column;
}
.gfield.column-inline input{
    padding: 0!important;
}
/* Form Title */

.gform_title {
    display: none;
}

.sub_step_title {
    line-height: 1em;
}

/* Steps */

.gform-theme--framework .gf_page_steps {
    margin-bottom: 48px !important;
}

.gform-theme--foundation .gf_page_steps{
    justify-content: center;
}


.gform-theme--foundation .gf_page_steps{
       gap: 8px !important; 
}

span.gf_step_number {
font-size: 16px !important;
padding: 16px;
}

.gf_step_pending span.gf_step_number {
    border-color: #ffffff;
    color: #0D6986;
    background-color: #ffffff;
}

.gf_step_active span.gf_step_number {
    border-color: #0D6986;
    color: #0D6986;
    background-color: #ffffff00;
}

.gf_step_complete span.gf_step_number {
    background-color: #07485b;
    color: #ffffff;
}

.gf_step_completed span.gf_step_number {
    background-color: #07485b;
    border-color: #07485b;
    color: #ffffff;
}

span.gf_step_number::after {
    background-color: #07485b !important;
    border-color: #07485b !important;
    font-size: 16px !important;
padding: 16px;
}

/* Step Inner Headings */

h2.step_heading {
    color: #07485b;
    margin-bottom: 8px;
}

span.sub_step_title {
    font-size: 24px;
}

div#field_13_88,
div#field_13_89{
    padding-bottom: 0px;
    margin-bottom: 32px;
}
div#field_13_88 h3,
div#field_13_89 h3{
    margin-bottom: 0px;
    padding-bottom: 16px;
}
#field_13_137, #field_13_180, #field_13_182{
    margin-bottom:24px;
}
#field_13_102, #field_13_103, #field_13_53{
    margin-bottom: 64px;
}
#field_13_137{
    margin-top: 32px;
}
#field_13_180{
    margin-top: 48px;
}
#field_13_53{
    grid-column: span 3;
}
/* Fields */
#field_13_146 .gfield_label {
    font-size: 18px !important;
    font-weight: 600 !important;
    margin-bottom: 3px;
    color: #07485b !important
}

#gfield_description_13_146, #field_13_85 {
    font-size:16px;
    color:black;
}

div.non-interact input{
    user-select: none;
    pointer-events: none;
}
.booking_form div.non-interact.disabled.highlight input:not([type="button"]):not([type="submit"]){
    background-color:#0d6986;
    color: white!important;
}
.booking_form div.non-interact.disabled input:not([type="button"]):not([type="submit"]){
    background-color:#eaeae8;
    padding: 15px 32px!important;
    border-radius: 10px;
}
.booking_form label.gfield_label.gform-field-label {
    font-size: 16px;
}

.booking_form input:not([type="button"]):not([type="submit"]){
    height: 64px;
    background-color: #fff;
    border-radius: 8px;
    width: 100%;
    color: #0D6986!important;
    font-weight: 600!important;
    font-size: 18px!important;
    width: 100%!important;
}

#input_13_34_1, #input_13_36_1, #input_13_38_1, #input_13_37_1, #input_13_39_1 {
    height: 50px;
}

#gform_fields_13_2 label.gfield_label,
#gform_fields_13_2 label.gfield_label span,
#field_13_133 .gfield_label,
#field_13_134 .gfield_label,
#field_13_135 .gfield_label,
#field_13_147 .gfield_label,
#field_13_115 .gfield_label{
    font-size: 18px!important;
    font-weight: 600!important;
    margin-bottom:3px;
    color:#07485b!important;
}

#field_13_34 .gfield_label_product .FB-product-description,
#field_13_36 .gfield_label_product .FB-product-description,
#field_13_37 .gfield_label_product .FB-product-description,
#field_13_38 .gfield_label_product .FB-product-description,
#field_13_39 .gfield_label_product .FB-product-description
{
    font-weight:400!important;
    font-size: 16px!important;
    color: black !important;
}

#ginput_product_price_13_34 span,
#ginput_product_price_13_36 span,
#ginput_product_price_13_38 span,
#ginput_product_price_13_37 span,
#ginput_product_price_13_39 span
{
    font-size:16px !important;
    color:black !important;
    font-weight:400;
}

#field_13_177 .gfield_label{
    display:none;
}
#field_13_177, #field_13_182{
    margin-top: 48px;
}
#gfield_description_13_34,
#gfield_description_13_36,
#gfield_description_13_38,
#gfield_description_13_37,
#gfield_description_13_39,
#field_13_34 .ginput_quantity_label,
#field_13_36 .ginput_quantity_label,
#field_13_38 .ginput_quantity_label,
#field_13_37 .ginput_quantity_label,
#field_13_39 .ginput_quantity_label{
    font-size: 16px;
    font-weight: 400;
    margin: 0px;
}
#field_13_34 .ginput_quantity_label,
#field_13_36 .ginput_quantity_label,
#field_13_38 .ginput_quantity_label,
#field_13_37 .ginput_quantity_label,
#field_13_39 .ginput_quantity_label{
    margin-bottom: 4px;
}
.gform-theme--framework .gfield--type-product .ginput_product_price{
    font-weight:600;
}
div.ginput_product_price_wrapper span{
    font-size: 18px!important;
    color:#07485b!important;
}

#gform_fields_13_4 #field_13_59 {
    grid-column: span 8;
}

#gform_fields_13_4 #field_13_53 {
    grid-column: span 4;
}

#field_13_59 .gform-field-label {
    font-size:16px;
}

#field_13_102, #field_13_103, #field_13_53 {
    margin-bottom:15px;
}

#input_13_125_4, #input_13_126_4 {
    height: 64px;
    border-radius: 8px;
}

.FB-product-description {
    
}

/*Request a callback page*/
#gform_page_13_4 #gform_fields_13_4 #field_13_59 {
    grid-column:span 6;
}
#gform_page_13_4 #gform_fields_13_4 #field_13_53 {
    grid-column:span 3;
}

select {
    color: #0D6986 !important;
    font-weight: 600 !important;
    font-size: 18px !important;
    align-items: center!important;
}

#field_13_185 .charleft,
#field_13_185 .gfield_description {
    font-size:16px;
    color:#585e6a;
}

/*Page 4 Contact Information */

#input_13_125_1_container, #input_13_125_2_container, #input_13_126_1_container, #input_13_126_2_container {
    width: 50%;
}

#input_13_125_3_container, #input_13_125_4_container, #input_13_125_5_container, #input_13_126_3_container, #input_13_126_4_container, #input_13_126_5_container {
    width: 33%;
}

/* Page 4 Buttons */
#gform_page_13_4 .gform-page-footer #gform_submit_button_13:hover {
    background-color:#b02b2c!important;
    color:white!important;
}

#gform_previous_button_13:hover {
    background-color:white;
}

/*** Blue Box Fields ***/
div.blue-box-top{
    background-color: #07485B;
    padding: 52px 52px 1px 52px!important;
    border-radius: 15px 15px 0px 0px;
}
div.blue-box-top h3{
    color: white;
    font-size: 32px;
}
div#field_13_81.blue-box-bottom{
    padding-bottom:64px!important;
    border-radius: 0 0 15px 15px;
}
div#field_13_81.blue-box-bottom input{
    background-color: transparent;
    padding: 0;
    font-size: 24px!important;
    color: white!important;
}
#field_13_128, #field_13_129,
#field_13_72, #field_13_143,
#field_13_75, #field_13_81, #field_13_155 {
    background-color: #07485B;
    padding-top: 20px;
    padding-inline: 52px;
    padding-bottom: 32px!important;
}

#field_13_128 label.gfield_label, #field_13_129 label.gfield_label, #field_13_72 label.gfield_label, #field_13_143 label.gfield_label, #field_13_75 label.gfield_label, #field_13_81 label.gfield_label,
    #field_13_155 label.gfield_label
    {
    font-size: 18px!important;
    color: white!important;
}

#field_13_128 input, #field_13_129 input, #field_13_72 input, #field_13_143 input, #field_13_75 input, #field_13_81 input,
    #field_13_155 input{
    width: 100%;
    background-color: #0D6986;
    border-radius: 10px;
    color: white!important;
    font-size: 18px;
    padding: 15px 32px;
}

#field_13_133,
#field_13_134,
#field_13_135,
#field_13_147{
    grid-column: span 12;
    margin-top: 16px;
}
#field_13_59, #field_13_53{
    grid-column: span 12;
}

#field_13_155 .gfield_description, #field_13_75 .gfield_description{
    color:white;
    font-size:16px;
}

#field_13_128 .gfield_required {
    display:none!important;
}

/* Field Descriptions */
.gform-body .gform_page_fields .gfield_description{
    font-size:18px;
    font-weight:500;
    color:#07485b;
}

#field_13_125 .gform-field-label {
    font-size: 16px;
}

#field_13_188 label {
    margin-top:14px;
    font-size: 18px !important;
    font-weight: 600 !important;
    margin-bottom: 3px;
    color: #07485b !important;
}

/* Buttons */
.gform-page-footer.gform_page_footer{
    justify-content: space-between!important;
}
.gform-page-footer.gform_page_footer .gform_next_button.gform-theme-button.button{
    margin-left:auto!important;
}
.gform-page-footer.gform_page_footer .gform_next_button.gform-theme-button.button,
.gform-page-footer.gform_page_footer .gform_previous_button.gform-theme-button.button{
    padding: 30px 50px!important;
    border-radius: 40px!important;
    font-size: 18px!important;
}
.gform-page-footer.gform_page_footer .gform_next_button.gform-theme-button.button{
    background-color: #B02B2C!important;
}
.gform-page-footer.gform_page_footer .gform_previous_button.gform-theme-button.button{
    border: 1px solid #07485b;
}
#field_13_52 h3{
    margin: unset;
}
#FB-BUTTON.popmake-37378.pum-trigger{
    background-color: #EAEAE8;
    color: #07485B;
    padding: 32px 24px;
    border-radius: 14px;
    cursor: pointer;
    margin-top: 20px;
    width: 100%;
    font-size: 16px;
    justify-content: flex-start;
    font-weight: 700;
}

#FB-BUTTON.popmake-37378.pum-trigger span {
    color:#B02B2C;
    font-weight: 500;
}

#field_13_60 .gchoice,
#field_13_115 .gchoice{
    align-items: center;
    gap: 16px;
    padding:16px;
    border: 1px solid #07485b;
    border-radius: 10px;
}
#field_13_60 .gchoice:has(input:checked),
#field_13_115 .gchoice:has(input:checked){
    background-color:#07485b;
}
#field_13_60 .gchoice:has(input:checked) label,
#field_13_115 .gchoice:has(input:checked) label{
    color: white;
}
#field_13_60 .gchoice input,
#field_13_115 .gchoice input{
    height:32px;
    aspect-ratio: 1 / 1;
}
#field_13_60 .gchoice input:checked,
#field_13_115 .gchoice input:checked{
    border-color: white;
    background-color: #fc7c5b;
}
#field_13_60 .gchoice input:before,
#field_13_115 .gchoice input:before{
    content: unset!important;
}
#field_13_60 .gchoice label{
    font-size:20px;
    font-weight: 500;
}
#field_13_60 .gchoice label div,
#field_13_115 .gchoice label{
    font-size:16px;
    font-weight: 400;
}

