.gform-page-footer.gform_page_footer.top_label {
    justify-content: center;
}

body .gform_wrapper .booking_form_wrapper .gform_body .gform_page_footer inout.gform_next_button {
background-color: #b02b2c;
    padding: 16px 32px;
    border-radius: 32px;
    font-size: 18px;
}

*/
/* Random elements */

div#field_18_85 {
    font-size: 16px;
}

/*.gfield_required.gfield_required_text{*/
/*    display:none!important;*/
/*}*/
.gform-loader{
    position: absolute!important;
    right: 8px!important;
    bottom: 8px!important;
}

form input[type="submit"]#gform_submit_button_13{
    padding: 30px 50px!important;
    border-radius:40px!important;
    font-size:18px!important;
    border: 2px solid #b02b2c!important;
}
form input[type="submit"]#gform_submit_button_13:hover{
    color: #b02b2c!important;
}
@media (max-width: 1149px){
    #gform_fields_13_2{
        column-count: 1;
    }
    .column-break{
    break-before: unset!important;
    }
    #field_13_88{
        margin-top: 32px;
    }
    #field_13_70,
    #field_13_69,
    #field_13_86{
        width: 100%;
    }
}
.gform_next_button, .gform_previous_button{
    margin-top:30px!important;
}

#field_13_134, #field_13_133, #field_13_135, #field_13_147 {
    grid-column: span 3;
}

#gform_submit_button_13, #gform_previous_button_13 {
    height:80px;
    margin-top:30px;
}

/* Calendar */

.ui-datepicker .ui-datepicker-calendar .ui-datepicker-unselectable {
     background:#B02B2C;
 }
 
 .ui-datepicker .ui-datepicker-calendar .ui-datepicker-unselectable .ui-state-default {
     color:#FFF;
 }
 
 .ui-datepicker-prev:hover, .ui-datepicker-next:hover {
     cursor:pointer;
 }
 
 /* Book Pickup Thank You Page */
 
#gform_confirmation_message_13 .thank-you-row {
    background-color: white;
    padding: 30px;
    border-radius: 10px;
    margin-top: 70px;
    margin-bottom: 70px;
}

#gform_confirmation_message_13 .thank-you-row .title-wrapper {
    text-align:center;
}

#gform_confirmation_message_13 .thank-you-row h3 {
    text-align: center;
    max-width: 700px;
    margin: auto;
    margin-bottom:50px;
}
 
#gform_confirmation_message_13 .thank-you-item {
     width: 33%;
    display: inline-block;
}
 
#gform_confirmation_message_13 .thank-you-item h4 {
    font-size: 18px;
    color: #07485B;
    
}
  
#gform_confirmation_message_13 .thank-you-item p {
    background-color: lightgray;
    width: 90%;
    padding-left: 20px;
    border-radius: 10px;
}

#gform_confirmation_message_13 .second-thank-you-row{
    background-color: #3DB692;
    border-radius: 10px;
    padding: 80px 40px;
}

#gform_confirmation_message_13 .second-thank-you-row .title {
    width: 210px;
    display: inline-block;
    vertical-align: top;
    margin-right: 100px;
}

#gform_confirmation_message_13 .second-thank-you-row .title h3 {
    color: white;
}

#gform_confirmation_message_13 .second-thank-you-row .ordered-list {
    color: white;
    display: inline-block;
}

#gform_confirmation_message_13 .second-thank-you-row .ordered-list ol{
    margin-top:0px;
    margin-bottom:0px;
}

/*** Mobile Styling ***/

@media only screen and (max-width: 600px) {
    /* Overall body padding */
    div.gform-body {
        padding:40px!important;
    }
    h1 {
        margin-top:26px!important;
    }
    
    /* Buttons */
    .button {
        line-height:1.4em!important;
    }
    
    div.gform-page-footer {
        flex-direction:column;
    }
    #gform_previous_button_13_41,
    #gform_previous_button_13,
    #gform_previous_button_13_61
    {
        order:2;
        padding: 20px !important;
        width: 300px;
        margin: auto;
        font-size:12px!important;
        height:unset;
    }
    #gform_next_button_13_41,
    #gform_submit_button_13,
    #gform_next_button_13_61
    {
        order:1;
        margin:auto;
        width:100%;
    }
    
    /* Blue Box Items */
    div.blue-box-top {
        padding:20px 20px 1px 20px !important;
    }
    #field_13_128, #field_13_155, #field_13_72, #field_13_143, #field_13_75, #field_13_81 {
        padding-left: 20px!important;
        padding-right: 20px !important;
    }
    
    /*Request a Callback Page */
    #field_13_134,
    #field_13_147,
    #field_13_135,
    #field_13_59,
    #field_13_53 {
        grid-column:span 12!important;
    }
    
    /* Pickup and Pay Page */
    #field_13_125 span,
    #field_13_126 span{
        width: 100%;
    }
    
    /* Pickup and Pay Thankyou Page */
    div.thank-you-item {
        width:100%;
    }
}
